INSERT INTO `sanpham` (`masanpham`, `tensanpham`, `gia`, `soluong`, `anhurl`, `mota`, `madanhmuc`, `maloai`) VALUES
(000001, 'Cây xạ đen - Vị thuốc quý điều trị ung thư', '150000', 42, 'https://vcdn-vnexpress.vnecdn.net/2019/06/06/la-xa-den-chua-ung-thu-va-HIV-5587-1559794826.jpg', 'Cây xạ đen là loại thảo dược quý mà thiên nhiên ban tặng. Với công năng hỗ trợ điều trị Ung thư, giúp giải độc gan, triệt tiêu các khối u bướu, cây xạ đen được tin tưởng sử dụng rộng rãi trong Y học cổ truyền và hiện đại. ', 2, 1),
(000002, 'Chè Dây -Hỗ trợ điều trị viêm loét dạ dày tá tràng\r\n', '150000', 3, 'https://vitas.org.vn/wp-content/uploads/2021/07/cong-dung-che-day-rung.jpg', 'Chè dây có giá trị rất quý về mặt dược liệu, các nghiên cứu khoa học cho thấy chè dây rất tốt cho những người có bệnh lý về dạ dày. Thảo dược này cũng giúp an thần, hỗ trợ ổn định huyết áp, thanh nhiệt tiêu viêm vô cùng hiệu quả. \r\n', 5, 1),
(000003, 'Chè Bồ Công Anh - Thanh lọc cơ thể', '150000', 54, 'https://bizweb.dktcdn.net/100/025/663/files/111-jpeg.jpg?v=1626192616026', 'Bồ công anh không chỉ đơn thuần là một loài hoa mang thông điệp tích cực từ cuộc sống, loài thực vật này còn có công dụng hữu ích cho cơ thể con người. Bồ công anh giúp giải độc gan, thanh lọc cơ thể, phòng ngừa Ung thư. Chiết xuất lành tính trong bồ công anh vô cùng thích hợp để mọi người sử dụng thay trà hàng ngày.', 1, 1),
(000004, 'Cây cam thảo đất - bài thuốc hay trị nhiều bệnh', '100000', 41, 'https://www.thuocdantoc.org/wp-content/uploads/2019/11/cay-cam-thao-dat.1.jpg', 'Cam thảo đất là vị thuốc Nam lấy từ cây cam thảo đất, có tác dụng ngăn ngừa biến chứng tiểu đường, chữa viêm họng, ho, giúp thanh nhiệt, lợi tiểu và nhiều công dụng khác.', 1, 1),
(000005, 'Nụ Hoa Tam Thất – Hỗ trợ điều trị mất ngủ, tốt cho tim mạch\r\n', '900000', 19, 'https://images.baodantoc.vn/thumbs/600x315/uploads/2022/Th%C3%A1ng%206/Ng%C3%A0y%207/Thanh/hoa-tam-that.jpg', 'Nụ hoa tam thất không chỉ đẹp mà còn mang lại rất nhiều công dụng tốt cho sức khỏe. Hoa tam thất được biết đến với chức năng giúp an thần, giải tỏa căng thẳng, tạo giấc ngủ sâu. Ngoài ra, còn giúp bổ máu, tiêu ứ huyết, lợi sữa, đặc biệt tốt với phụ nữ sau sinh. \r\n', 1, 3),
(000006, 'Giảo Cổ Lam – Giúp ổn định huyết áp, giảm mỡ máu', '150000', 52, 'https://giongcaytrong.org/uploads/shops/2016_10/giao-co-lam.jpg', 'Giảo cổ lam là dược liệu quý hiếm được sử dụng rộng rãi trong Tây y và y học cổ truyền. Loài thảo dược này được mệnh danh là cây thuốc trường sinh bởi công năng hỗ trợ điều trị hạ mỡ máu, tăng cường sức đề kháng, phòng ngừa các tai biến do tim mạch. ', 5, 1),
(000007, 'Hạt thì là - Bí kíp giảm cân, đẹp da và nhiều tác dụng tuyệt vời', '150000', 42, 'https://file.hstatic.net/1000387874/file/hat-thi-la-1_8de05ac6a75c495c8758ea6c0e924d09_grande.jpg', 'Hạt thì là không chỉ làm gia vị khử mùi tanh trong đồ ăn, trà hạt thìa được xem là thức uống có tác dụng giúp ngủ ngon, đẹp da, giảm cân và lợi sữa tuyệt vời.', 1, 1),
(000008, 'Cây Mú Từn – Thảo dược tăng cường sinh lý, chồng uống vợ mê', '120000', 53, 'https://taybacstore.com/wp-content/uploads/2023/02/cay-mu-tun.png', 'Mú Từn là loại vị thuốc quý có tác dụng tráng dương, bổ thận, kiện gân cốt, bài trừ phong thấp, đặc biệt rất tốt cho sinh lý cơ thể. Cây Mú Từn ngâm có màu đỏ nâu, vị đặm đà hơi chát, mùi rất thơm, được nhiều quý ông ưa thích và sử dụng.', 1, 1),
(000009, 'Hoa Đu Đủ Đực – Bài thuốc dân gian hỗ trợ điều trị Ung thư', '800000', 43, 'https://static.kinhtedothi.vn/w960/images/upload/2022/08/03/964fe575f5d91369f09087c8884ddecb.png', 'Hoa đu đủ đực là một loại dược liệu khá quý hiếm với nhiều công dụng đặc biệt. Chiết xuất đặc biệt từ hoa đu đủ đực giúp hỗ trợ phòng ngừa Ung thư, giảm thiểu u bướu, kích thích tiêu hóa, góp phần xây dựng một cơ thể khỏe mạnh.', 1, 3),
(000010, 'Sâm Cau Đỏ – “Thần dược” tự nhiên giúp bổ dương mạnh gân cốt', '150000', 45, 'https://cdn.medigoapp.com/product/1560584596_ba0ffc588f1189cbfd1eb4fb2daa4dd4_e7014939c1.jpg', 'Sâm cau đỏ là thượng phẩm thiên nhiên ban tặng riêng cho các quý ông. Với công năng bổ thận, tăng cường chức năng sinh lý, tăng cường sức khỏe phái mạnh, khi ngâm có nhiều tác dụng tốt cho sức khỏe. Sâm cau đỏ có mùi thơm đặc trưng, vị ngọt nhẹ, được nhiều quý ông ưa thích và sử dụng.', 1, 1),
(000011, 'Cây An Xoa – Vị thuốc quý trị ung thư gan, xơ gan cổ trướng\r\n', '120000', 58, 'https://vinmec-prod.s3.amazonaws.com/images/20211201_142453_015112_cay-an-xoa-co-tac-d.max-1800x1800.jpg', 'Theo Y học cổ truyền cây an xoa là cây thuốc quý sở hữu nhiều tác dụng đối với gan, hỗ trợ điều trị vô cùng hiệu quả trong việc giải độc gan, giúp tăng cường chức năng gan.\r\n', 2, 2),
(000012, 'Kỷ Tử – Vị thuốc quý giúp sáng mắt, tăng cường sinh lý\r\n', '350000', 38, 'https://vinmec-prod.s3.amazonaws.com/images/20211206_034428_026755_an-ky-tu.max-1800x1800.jpg', 'Là một trong những vị thuốc quý có nhiều công dụng tốt cho sức khỏe. Kỷ tử là vị thuốc được sử dụng phổ biến trong y học cổ truyền để trị các bệnh về mắt, vô sinh hiếm muộn, di mộng tinh ở nam giới hay viêm dạ dày mãn tính.\r\n', 9, 3),
(000013, 'Cây Rau Mương – Thuốc quý trị đau dạ dày, diệt vi khuẩn HP\r\n', '150000', 29, 'https://vinmec-prod.s3.amazonaws.com/images/20211215_143339_085705_cong-dung-cua-cay-r.max-1800x1800.jpg', 'Theo Y học cổ truyền, rau mương là loại dược liệu có vị ngọt nhạt, tính mát có tác dụng thanh nhiệt giải độc, tiêu sưng. Rau mương trong nhân dân thường được sử dụng làm thuốc điều trị đau dạ dày có H.Pylori và rất hiệu quả.\r\n', 5, 4),
(000014, 'Khổ Qua Rừng – Giảm cholesterol, hỗ trợ điều trị tiểu đường\r\n', '350000', 24, 'https://cdn.tgdd.vn/Files/2020/11/14/1306766/nhung-cong-dung-tuyet-voi-cua-kho-qua-rung-doi-voi-suc-khoe-202011141939077517.jpg', 'Khổ qua rừng được mệnh danh một loại thảo dược rất tốt cho người bị bệnh tiểu đường, cây thuốc mọc hoang nhưng mang lại tác dụng không ngờ tới. Khổ qua rừng đã thành công làm giảm bớt âu lo của con người trong hỗ trợ điều trị cao huyết áp, bệnh tiểu đường type II.\r\n', 4, 4),
(000015, 'Dây Thìa Canh – Thảo dược giúp hạ đường huyết và mỡ máu\r\n', '150000', 13, 'https://vinmec-prod.s3.amazonaws.com/images/20211127_173359_832074_tac-dung-cua-day-th.max-1800x1800.jpg', 'Dây thìa canh là loại dược liệu quý rất tốt cho những bệnh nhân bị tiểu đường, giảm hấp thu đường từ ruột vào máu, tăng sự dụng đường tại các mô cơ. Ngoài ra, thìa canh còn được biết đến như một loại thảo dược tốt cho tiêu hóa, phòng ngừa đau xương tê bại hiệu quả. \r\n', 8, 4),
(000016, 'Chuối Hột Rừng – Tốt cho bệnh sỏi thận\r\n', '120000', 43, 'https://amthuctaybac.net/wp-content/uploads/2022/03/qua-chuoi-hot-rung-song.jpg', 'Chuối hột rừng là một loại thực vật rất có giá trị về mặt dược liệu. Đây là vị thuốc có tác dụng kích thích tiêu hóa, bài trừ sỏi thận, tăng cường thanh lọc cơ thể.\r\n', 6, 4),
(000017, 'Cây ngải cứu - Công dụng chữa bệnh tuyệt vời\r\n', '120000', 19, 'https://icdn.dantri.com.vn/zoom/1200_630/2019/08/23/ngai-cuu-1566519242513.jpg', 'Ngải cứu là loại rau ăn có tác dụng bổ huyết, giúp an thai, uống nước cây ngải cứu có công dụng hỗ trợ điều kinh, chữa đau xương khớp, trị ho, cảm cúm, thiếu máu,… Có thể nói, rau ngải cứu là vị thuốc quý chữa đa bệnh, tốt cho sức khỏe, ít loại rau dại nào sánh bằng.\r\n', 11, 3),
(000018, 'Nấm Lim Xanh – Thảo dược quý từ thiên nhiên ban tặng\r\n', '1200000', 19, 'https://cdn.tgdd.vn/2021/03/CookProduct/Tac-dung-cua-nam-linh-xanh-gia-ban-va-cach-dung-an-toan-nhat-0-1200x676.jpg', 'Nấm Lim Xanh – loài thượng phẩm bổ dưỡng với nhiều thành phần quý giá có ảnh hưởng tích cực đến sức khỏe. Không chỉ hữu hiệu trong việc hỗ trợ điều trị Ung thư, nấm lim xanh còn giúp hỗ trợ điều trị các bệnh lý về gan, giúp tăng cường sức khỏe vô cùng hiệu quả.\r\n', 3, 3),
(000019, 'Trái Nhàu Khô – Chữa phong thấp, đau lưng, nhức mỏi tay chân', '150000', 34, 'https://cdn.tgdd.vn/Files/2018/04/12/1081621/cong-dung-cua-trai-nhau-khong-phai-ai-cung-biet-202206061440096925.jpg', 'Trái nhàu khô là một loại quả đa dạng về dinh dưỡng và chức năng dược lý, là tặng phẩm quý giá đến từ thiên nhiên. Trái nhàu khô vô cùng hữu ích đối với những người bị phong tê thấp, bệnh gout hay loãng xương, giúp tinh thần phấn chấn, lạc quan, xóa tan mệt mỏi. ', 1, 1),
(000020, 'Xuyên tâm liên - Kháng sinh thực vật dùng để điều trị COVID-19\r\n', '200000', 14, 'https://namlimxanh.vn/wp-content/uploads/2018/06/dac-diem-cua-cay-xuyen-tam-lien-va-tac-dung-cach-dung-xuyen-tam-lien.jpg', 'Xuyên tâm liên là một vị thuốc kháng sinh thực vật, dùng để hạ sốt, trị viêm họng, viêm đường hô hấp, tiêu chảy hay dùng để giải độc. Gần đây, Việt nam đã đưa Xuyên Tâm Liên vào làm thuốc hỗ trợ điều trị COVID-19.', 2, 2),
(000021, 'Cây tầm bóp - Cách sử dụng rau tầm bóp chữa bệnh.\r\n', '120000', 54, 'https://sinhduoc.vn/wp-content/uploads/2020/04/tacdungcuacaytamboplagi1_20190318152159.jpg', 'Cây tầm bóp là loại rau dại mọc lan ở khắp các vùng nông thôn. Vì vậy nên ít người biết rằng đây còn là vị thuốc quý với nhiều công dụng chữa bệnh, trong đó có ung thư.\r\n', 10, 2),
(000022, 'Trà Hoa Cúc – Giúp thanh nhiệt, giải độc, mát gan và đẹp da\r\n', '600000', 21, 'https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/2/25/883455/Tra-Mat-Ong-Hoa-Cuc.jpg', 'Trà hoa cúc là loại thức uống có công dụng tuyệt vời đối với sức khỏe. Vị trà tính mát giúp giải tỏa căng thẳng, thanh nhiệt tiêu độc. Trà hoa cúc được người Việt Nam đón nhận như một hình thức trà đạo, thưởng thức chậm rãi cuộc sống, tĩnh tâm và đầy nghệ thuật. \r\n', 1, 3),
(000023, 'Chè Vằng Sẻ – Lợi sữa, giảm cân cho phụ nữ sau sinh\r\n', '150000', 39, 'https://cdn.tgdd.vn/Files/2021/08/04/1372996/che-vang-co-loi-sua-khong-cach-dung-nhu-the-nao-202206031115122226.jpeg', 'Chè vằng sẻ được sử dụng rộng rãi trong Y học cổ truyền và Y học hiện đại. Thảo dược chè vằng có tác dụng thanh nhiệt, giải độc, mát gan. Ngoài ra còn kích thích tiết sữa, giảm mỡ bụng, đặc biệt tốt đối với phụ nữ sau sinh.\r\n', 7, 1),
(000024, 'Cây Cối Xay – Vị thuốc quý chữa xương khớp, bệnh trĩ\r\n', '120000', 21, 'https://omega3.vn/wp-content/uploads/2021/05/cay-coi-xay-tuoi.jpg', 'Cây cối xay là vị thuốc dân gian hỗ trợ điều trị nhiều bệnh lý khác nhau như: xương khớp, gan, thận, trĩ… Đồng thời giúp thải độc, mát gan hiệu quả.\r\n', 11, 1),
(000025, 'Trà Lá Sen – Giúp thải độc gan, giảm cân, điều trị mỡ máu\r\n', '120000', 18, 'https://media.loveitopcdn.com/1229/cach-lam-tra-la-sen-don-gian-tai-nha.jpg', 'Trà lá sen là một loại trà đặc biệt có vị hơi chát, mùi thơm nhẹ, giúp an thần giải độc vô cùng hiệu quả. Vị trà này phù hợp với người muốn giảm béo, người có huyết áp không ổn định. Ngoài ra, trà lá sen cũng giúp hỗ trợ giấc ngủ, an thần.\r\n', 7, 1),
(000026, 'Cây Cỏ Xước – Vị thuốc dân gian trị sỏi thận, đau xương khớp\r\n', '120000', 18, 'https://cdn.nhathuoclongchau.com.vn/unsafe/filters:quality(95)/https://cms-prod.s3-sgn09.fptcloud.com/cay_co_xuoc_c845d62d6f.jpg', 'Cỏ xước là một loại thảo dược sử dụng rộng rãi trong dân gian. Vị thuốc có tác dụng hỗ trợ điều trị suy thận, chức năng thận kém, ngoài ra những người đau nhức xương khớp, viêm khớp cũng mang lại tác dụng vô cùng hiệu quả.\r\n', 10, 1),
(000027, 'Cây dừa cạn- Công dụng chữa ung thư và cách dùng gây ngỡ ngàng\r\n', '650000', 21, 'https://cdn.tgdd.vn/Files/2021/07/23/1370301/cach-cham-soc-va-cong-dung-it-ai-ngo-den-cua-cay-hoa-dua-can-202107231453028460.jpg', 'Cây dừa cạn (bông dừa cạn) là dược liệu có tác dụng hỗ trợ điều trị ung thư, tiểu đường, cao huyết áp. Cùng tìm hiểu hình ảnh, công dụng, cách dùng cây hoa dừa cạn \r\n', 4, 3),
(000028, 'Hoa Hòe – Ổn định huyết áp, ngăn ngừa tai biến mạch máu não\r\n', '350000', 22, 'https://suckhoedoisong.qltns.mediacdn.vn/Images/nguyenkhanh/2017/12/28/hoe_hoa.jpg', 'Cây hoa hòe từ lâu được trồng làm cây cảnh trong nhiều gia đình Việt Nam. Không chỉ là cây phong thủy, loại cây này còn cho nụ hoa có giá trị rất cao về mặt dược liệu. Nụ hoa hòe tinh chế thành trà giúp ổn định huyết áp, ngăn ngừa tai biến mạch máu não\r\n', 11, 1),
(000029, 'Lá Khôi – Thảo dược trị bệnh đau dạ dày, viêm loét dạ dày hiệu quả\r\n', '450000', 15, 'https://thuocdantoc.vn/wp-content/uploads/2019/05/la-khoi-tia-chua-trao-nguoc-da-day-02.jpg', 'Trong Đông y, lá khôi là một vị thuốc quý có hiệu quả đặc biệt trong điều trị bệnh đau dạ dày, viêm loét dạ dày tá tràng, ngoài ra còn giúp làm se vết loét, làm lành dạ dày và tá tràng nhanh chóng.\r\n', 5, 3),
(000030, 'Cỏ Mần Trầu – Vị thuốc đẹp da, bổ máu, chữa nhiều bệnh\r\n', '120000', 19, 'https://vinmec-prod.s3.amazonaws.com/images/20220103_080309_867173_co-man-trau.max-800x800.jpg', 'Cỏ mần trầu là loại thảo dược quý, thân thuộc với người dân quê của Việt Nam. Loại dược liệu này giúp làm đẹp da, bổ máu, chữa nhiều bệnh, mang lại sức đề kháng tốt cho người dùng.\r\n', 4, 1),
(000031, 'Bạch Hoa Xà Thiệt Thảo – Ngăn ngừa u bướu, điều trị Ung thư\r\n', '200000', 17, 'https://vinmec-prod.s3.amazonaws.com/images/20211130_095603_399974_bach-hoa-xa-1.max-1800x1800.jpg', '', 8, 1),
(000032, 'Cây Lạc Tiên – ‘Thần dược’ chữa bệnh mất ngủ cực hiệu quả\r\n', '150000', 18, 'https://nongduocyenbai.com/wp-content/uploads/2022/04/cay-lac-tien.jpeg', 'Lạc tiên là một tặng phẩm quý giá mà thiên nhiên ban tặng cho sức khỏe. Thảo dược lạc tiên xóa tan nỗi lo mất ngủ đối với những người thường xuyên khó ngủ, ngủ không ngon dẫn tới suy nhược cơ thể, căng thẳng tinh thần.\r\n', 1, 1),
(000033, 'Đinh Lăng Nếp – Nhân sâm của người nghèo chữa bách bệnh\r\n', '200000', 13, 'https://trungtamduoclieu.com/wp-content/uploads/2020/08/dinh-lang-nep-2-255x255.jpg', 'Đinh lăng có tác dụng giúp an thần, bổ trợ giấc ngủ, giảm đau nhức xương khớp. Ngoài ra, rượu đinh lăng còn giúp bổ thận tráng dương, tăng cường chức năng sinh lý. Đây là vị thuốc với nhiều công dụng ưu việt, xứng danh nhân sâm giá rẻ của mọi nhà.\r\n', 11, 1),
(000034, 'Trà Tâm Sen – Trị mất ngủ, giảm béo, hạ đường huyết hiệu quả\r\n\r\n', '300000', 15, 'https://sendaiviet.com/wp-content/uploads/2022/01/tra-tim-sen-co-cong-dung-gi-trong-viec-chua-mat-ngu.jpg', 'Trà tâm sen là một loại trà đặc biệt có vị hơi đắng, mùi thơm nhẹ, giúp điều trị mất ngủ vô cùng hiệu quả. Sử dụng trà tâm sen mỗi ngày có tác dụng tác dụng an thần, hạ nhiệt trị mất ngủ, làm ổn định huyết áp và nhịp tim.\r\n\r\n', 10, 3),
(000035, 'Tam Thất Nam – Vị thuốc giảm đau nhức xương khớp, điều trị phong thấp\r\n\r\n', '300000', 22, 'https://benhvienphusanhaiphong.vn/Images/news/dieu-tri-vo-sinh-o-dau-tot-nhat-1.jpg', 'Củ Tam Thất Nam là vị thuốc lâu đời của người dân Việt Nam, có tác dụng giảm đau nhức xương khớp, điều trị phong tê thấp. Ngoài ra dược liệu này còn hỗ trợ điều trị cầm máu, giúp điều hòa băng huyết và chữa tiêu sưng.\r\n', 6, 1),
(000036, 'Cây Mật Nhân – Bổ thận tráng dương, trị đau lưng, nhức mỏi\r\n', '120000', 22, 'https://tracuuduoclieu.vn/wp-content/uploads/2021/09/cay-mat-gau-khac-cay-mat-nhan.jpg', 'Mật nhân là loại thảo dược quý mà thiên nhiên ban tặng cho tất cả mọi người. Với công năng bổ thận, hỗ trợ tăng cường chức năng sinh lý, giảm đau, bổ khí huyết.', 9, 1),
(000037, 'Dây Gắm – “Khắc tinh” của bệnh Gút (Gout) và Xương Khớp\r\n', '150000', 60, 'https://vinmec-prod.s3.amazonaws.com/images/20211214_154954_164609_cong-dung-cua-cay-g.max-1800x1800.jpg', 'Dây gắm là loại thảo dược có công năng trị Gout vô cùng hiệu quả. Chiết xuất đặc biệt của thảo dược này giúp giảm lượng acid uric tồn dư, cắt cơn đau xương khớp. Dây gắm là mang lại niềm tin cho dân tộc Tày, được sử dụng nhiều trong nhân dân trong việc điều trị gout, xương khớp.\r\n.\r\n', 6, 2),
(000038, 'Tinh Bột Nghệ Đỏ – “Thần dược” cho sắc đẹp và sức khỏe', '450000', 48, 'https://photo-cms-tpo.epicdn.me/w890/Uploaded/2023/rwbvhvobvvimsb/2021_10_10/tinh-bot-nghe-2-4203.jpg', 'Tinh bột nghệ đỏ Việt Nam từ lâu đã được sử dụng rộng rãi trong đời sống của con người với những công năng như phòng ngừa Ung thư, điều trị các bệnh dạ dày, hồi phục thương tổn. Ngoài ra đây cũng là một thảo dược tuyệt vời được sử dụng trong làm đẹp. ', 1, 3),
(000039, 'Dây tơ hồng vàng – Dây leo  nhiều công dụng ngỡ ngàng\r\n', '150000', 18, 'https://images.baodantoc.vn/uploads/2019/Ngay18thang11/to-hong.jpg', 'Dây tơ hồng là loại dây leo sống ký sinh trên cây khác. Thông thường hạt tơ hồng (thỏ ty tử) được biết đến nhiều hơn nhờ tác dụng tráng dương, cường thận cho nam giới.Tuy nhiên, bản thân dây tơ hồng cũng có tác dụng thanh nhiệt, lợi thủy, giải độc, dùng để chữa vàng da, rôm sảy ở trẻ em, giúp thông tiểu, chữa kiết lỵ và nhiều công dụng khác.\r\n', 5, 2),
(000040, 'Cây đuôi chuột - Trị ho, thanh nhiệt giải độc\r\n', '120000', 21, 'https://file1.dangcongsan.vn/data/0/images/2020/01/22/tuantran/dc1xdgsdf.jpg', 'Cây đuôi chuột là vị thuốc dân gian nổi tiếng với tác dụng hạ đường huyết, cải thiện tim mạch, trị viêm họng, ho. Trong Đông y, công dụng của cây đuôi chuột giúp thanh nhiệt, giải độc, lợi tiểu tiện và nhiều lợi ích cho sức khỏe.\r\n', 10, 2),
(000041, 'Cây Trinh Nữ (Cây xấu hổ)- An thần, hiệu quả bất ngờ trong việc trị xương khớp', '100000', 15, 'https://omega3.vn/wp-content/uploads/2021/11/hinh-anh-cay-trinh-nu.png', 'Cây trinh nữ hay cây xấu hổ, mắc cỡ, thường dùng ngâm rượu, sắc thuốc, nấu cao có tác dụng chuyên chữa các bệnh về xương khớp, suy nhược thần kinh, giúp an thần, ngủ ngon và nhiều công dụng tốt cho sức khỏe.', 1, 1),
(000042, 'Cây ích mẫu – Thuốc quý dành cho phụ nữ đau bụng kinh, tắc kinh\r\n', '120000', 0, 'https://vinmec-prod.s3.amazonaws.com/images/20211204_034653_817282_ich-mau-1.max-1800x1800.jpg', 'Ích mẫu là cứu tinh hàng đầu cho phụ nữ, có tác dụng phá ứ, phá huyết, thông kinh lạc, giúp điều kinh và chữa các bệnh phụ khoa cho phụ nữ rất hay, nhất là phụ nữ sau sinh. Vì có lá khá giống cây ngải cứu, lại có nguồn gốc từ phía Bắc nên còn gọi là Ngải cứu Trung Quốc.\r\n', 10, 2),
(000043, 'Cây rau bợ (cỏ bợ)- Rau dại đặc biệt, nhiều công dụng chữa bệnh\r\n', '100000', 10, 'https://bupxanh.vn/files/sanpham/272/1/jpg/co-bo.jpg', 'Câu ca lưu truyền từ xưa đã nói lên độ ngon “khó cưỡng” của món canh rau bợ. Cây rau bợ, hay còn gọi là cây cỏ bợ, tứ diệp thảo, cỏ may mắn. Là loài cỏ dại mọc chủ yếu ở các vùng đất ẩm, bờ ruộng, ven ao hồ.Không chỉ ăn ngon, rau bợ có nhiều công quý trong điều trị bệnh như: thanh nhiệt, giảm viêm, tiêu sưng, lợi tiểu, điều trị sỏi thận hiệu quả\r\n', 8, 2),
(000044, 'Cây cơm rượu – Thần dược trị đau xương khớp, tê thấp, tiểu đường\r\n', '150000', 8, 'https://www.thuocdantoc.org/wp-content/uploads/2019/11/cay-com-ruou-1.jpg', 'Cây cơm rượu còn gọi là bưởi bung, vị thuốc Nam sử dụng rộng rãi trong Đông y. Dược liệu có tính ấm, vị cay và hơi ngọt, chủ trị đau nhức xương khớp, tê thấp, kích thích tiêu hóa, hỗ trợ điều trị tiểu đường và một số bệnh khác. \r\n', 5, 2),
(000045, 'Rau diếp cá – thảo dược chữa nhiều bệnh\r\n', '120000', 12, 'https://cdn.tgdd.vn/2021/04/content/BeFunky-collage(3)-800x500-2.jpg', 'Rau diếp cá, hay rau dấp cá, giấp cá, vấp cá, nhíp cá, thuộc họ Saururaceae…. là loại rau ăn sống, không cần nấu chín, có vị tanh, cay nồng. Cây rau diếp được trồng ở một số nước Châu Á, trong đó có Việt Nam, Nhật Bản, Trung Quốc, Ấn Độ và Hàn Quốc.\r\n\r\n', 5, 4),
(000046, 'Lá tía tô -Uống nước lá tía tô có hiệu quả chữa bệnh không ngờ\r\n', '150000', 47, 'https://vinmec-prod.s3.amazonaws.com/images/20211206_120909_500600_tia-to-cho-ba-bau-4.max-1800x1800.jpg', 'Tía tô từ lâu đã không còn quá xa lạ đối với chúng ta, nó vừa làm nguyên liệu nấu ăn, vừa dùng làm thuốc, thường sử dụng xông mặt, đắp ngoài da, nấu nước uống có tác dụng giải cảm, trị ho, hạ sốt, trị mụn, nám và nhiều công dụng tuyệt vời khác.\r\n', 10, 3),
(000047, 'Cây mã đề - tác dụng lớn đối với sức khỏe\r\n', '100000', 36, 'https://suckhoedoisong.qltns.mediacdn.vn/324455921873985536/2022/1/11/photo-1641916891370-16419168929461510645815.jpg', 'Cây mã đề có rất nhiều công dụng tuyệt vời trong điều trị bệnh, nhất là các bệnh liên quan đến đường tiết niệu, tiêu hoá và thậm chí là các bệnh về da.', 10, 1),
(000048, 'Quả Sim Rừng – Hỗ trợ giảm đau nhức, giúp bổ máu, tráng dương', '150000', 15, 'https://asay.com.vn/wp-content/uploads/2021/11/sim-rung-gia-bao-nhiu.jpg', 'Là một trong những loại quả lên men tự nhiên tốt cho sức khỏe nên hiện nay được khá nhiều người sử dụng. Rượu sim rất dễ uống và có khả năng bổ thận tráng dương, tăng cường sức khỏe.', 3, 3),
(000049, 'Gừng gió – Thần dược chữa bệnh xơ gan, ung thư gan hiệu quả\r\n', '200000', 18, 'https://omega3.vn/wp-content/uploads/2021/06/cay-gung-gio.jpg', 'Gừng gió được dân gian ví như “tiên dược” có thể chữa được nhiều bệnh. Trong nhân dân, được mệnh danh là “thần dược” chữa xơ gan, viêm gan và hỗ trợ điều trị ung thư. Nhờ tiếng lành đồn xa, vị thuốc này đang ngày càng được nhiều săn lùng.', 2, 2),
(000050, 'Nhân trần - Công dụng và bài thuốc trị viêm gan từ cây nhân trần\r\n', '150000', 58, 'https://images.baodantoc.vn/uploads/2021/Th%C3%A1ng%206/Ng%C3%A0y%203/Thanh/nhan-tran-2.jpg', 'Nhân trần là dược liệu quý, thường dùng nấu nước, pha trà uống có tác dụng thanh nhiệt, điều trị viêm gan, vàng da, tiểu khó, mắt sưng đỏ,…', 2, 1);
